  const [userList, setUserList] = useState('')


  const addUserListHandler = (username) => {
    setUserList((prevUserNames) => {
      return [username, ...userNamesList]
    })
    console.log([...userNamesList]);
  }

  const addExpenseHandler = (expense) => {
    // adding new expense to expenses array and add to the dynamic list
    // spread into existing expenses and add expense argument from function
    setExpenses((prevExpenses) => {
      return [expense, ...expenses];
    });
  };
  return (
    <div>
      <AddUser userListHandler={addUserListHandler}/>
    </div>
  );
}

