// this component is to handle adding the user
import React, { useState } from "react";
import Card from "../UI/Card";
import "./AddUser.css";
import Button from "../UI/Button";

const AddUser = (props) => {
  const [addUsername, setAddUsername] = useState("");
  const [addAge, setAddAge] = useState("");

  const onAddUsername = (e) => {
    setAddUsername(e.target.value);


  };
  const onAddAge = (e) => {
    setAddAge(e.target.value);

  };

  const addUserHandler = (e) => {
    e.preventDefault();
    if(addUsername.length < 2){
        alert('please enter a username')
    }
    if(addAge < 12){
        alert('you must be 13 or older to use this site')
    }

    setAddUsername('');
    setAddAge('');
    console.log(Users)
  };
  const Users = [{
      id: Math.random().toString(),
      username: addUsername,
      age: addAge
  }];


  return (
    <Card className="form-control">
      <form className="form-control" onSubmit={addUserHandler}>
        <label className="label-control" htmlFor="username">
          Username:
        </label>
        <input
          className="input-control"
          id="username"
          type="text"
          value={addUsername}
          onChange={onAddUsername}
        />
        <label className="label-control" htmlFor="age">
          Age (years):
        </label>
        <input
          className="input-control"
          id="age"
          type="number"
          value={addAge}

          onChange={onAddAge}
        />
        <Button type="submit">Add User</Button>
      </form>
    </Card>
  );
};

export default AddUser;
