// this component is to handle adding the user
import React, { useState } from "react";
import Card from "../UI/Card";
import "./AddUser.css";
import Button from "../UI/Button";

const Users = [];

const AddUser = (props) => {


  const addUserHandler = (e) => {
    e.preventDefault();
    Users.push(addUsername);
    Users.push(addAge);
    console.log(Users)
  };
  const [ addUsername, setAddUsername ] = useState('');
  const [ addAge, setAddAge ] = useState('');

  const onAddUsername = (e) => {
      setAddUsername(e.target.value);
      console.log(addUsername);
  }
  const onAddAge = (e) => {
    setAddAge(e.target.value);
    console.log(addAge);
}


  return (
    <Card className="form-control">
      <form className="form-control" onSubmit={addUserHandler}>
        <label className="label-control" htmlFor="username">
          Username:
        </label>
        <input className="input-control" id="username" type="text" onChange={onAddUsername}/>
        <label className="label-control" htmlFor="age">
          Age (years):
        </label>
        <input className="input-control" id="age" type="number" onChange={onAddAge}/>
        <Button type="submit">Add User</Button>
      </form>
    </Card>
  );
};

export default AddUser;
