// this component is to handle adding the user
import React from 'react';
import Card from '../UI/Card';
import './AddUser.css';
import Button from './Button';
const AddUser = (props) => {

    const addUserHandler = (e) => {
        e.preventDefault();
    }


    return ( 
        <Card className="form-control">
        <form className="form-control" onSubmit={addUserHandler}>
            <label className="label-control" htmlFor="username">Username:</label>
            <input className="input-control"id="username" type="text" />
            <label className="label-control"htmlFor="age">Age (years):</label>
            <input className="input-control"id='age' type="number" />
        <Button type="submit">Add User</Button>
        </form>
        </Card>
     );
}

export default AddUser;
